const event = $json;

const isThread = !!event.thread_ts;
const session_id = isThread
  ? `${event.user}:${event.thread_ts}`
  : `${event.user}:top`;

const files = event.files || [];

return [{
  json: {
    session_id,
    user: event.user,
    text: event.text || "",
    ts: event.ts,
    thread_ts: event.thread_ts || null,
    is_thread: isThread,
    files // <== array of files stays bundled
  }
}];

const sessionId = items[0]?.json.session_id || null;

// Step 1: Extract and sort messages by TimeAdded
const sortedItems = [...items].sort((a, b) => {
    const aTime = a.json.message?.data?.TimeAdded || a.json.message?.data?.timeAdded;
    const bTime = b.json.message?.data?.TimeAdded || b.json.message?.data?.timeAdded;
    const aTimestamp = aTime ? new Date(aTime).getTime() : 0;
    const bTimestamp = bTime ? new Date(bTime).getTime() : 0;
    return aTimestamp - bTimestamp;
});

// Step 2: Prepare formatted message content and collect meta fields
const formattedMessages = [];
const addedContents = new Set(); // Track which messages we‚Äôve already added

let lastAddedAt = null;
let previousConversationChain = '';

for (let i = 0; i < sortedItems.length; i++) {
    const item = sortedItems[i];
    const data = item.json.message?.data || {};
    const content = data.content;
    const timeAdded = data.TimeAdded || data.timeAdded;
    const chain = data.previousConversationChain;

    // Skip empty or duplicate content
    if (content && !addedContents.has(content)) {
        formattedMessages.push(`--- Message ${formattedMessages.length + 1} ---\n${content}`);
        addedContents.add(content);
    }

    // Track most recent TimeAdded
    if (timeAdded) {
        const time = new Date(timeAdded).toISOString();
        if (!lastAddedAt || new Date(time) > new Date(lastAddedAt)) {
            lastAddedAt = time;
        }
    }

    // Track most recent non-null previousConversationChain
    if (chain && chain !== 'null') {
        previousConversationChain = chain;
    }
}

const combined = formattedMessages.join('\n\n');

// Final output
return [
    {
        json: {
            session_id: sessionId,
            message: {
                data: {
                    content: combined,
                    previousConversationChain: previousConversationChain || combined,
                    lastAddedAt: lastAddedAt || new Date().toISOString()
                }
            }
        }
    }
];
