// Extract items from previous node input and the original prompt
const inputs = $input.all();
const originalPrompt = $('Wait1').first().json.prompt;
const output = [];

for (let i = 0; i < inputs.length; i++) {
  const item = inputs[i];

  // Get the score and improvement feedback from the new structure
  const score = item.json.output?.Score || '';
  const improvementFeedback = item.json.output?.Improvement || '';

  output.push({
    json: {
      ...item.json,
      prompt: `${originalPrompt}\n\nQUALITY SCORE: ${score}\n\nIMPORTANT CORRECTIONS: ${improvementFeedback}\n\nEnsure photorealistic quality and accurate product representation.`,
      attempt: 2,
    },
  });
}

return output;
